from flask import Blueprint, render_template


users = [
    {
        "id": "1",
        "name": "John",
        "pic": "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTF3FUIWqvlCTuJzEb3juBvSd54uz1gn1SY8_RBJFUJBg&s",
        "pro": True,
    },
    {
        "id": "2",
        "name": "Carl",
        "pic": "data:image/jpeg;base64,",
        "pro": False,
    },
    {
        "id": "3",
        "name": "Sarah",
        "pic": "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRxQcpt-l3VPFa-mO-9DWhGNYUw5gHDjQ0klT0D-YJEkw&s",
        "pro": False,
    },
]


about_bp = Blueprint("about", __name__)


# decorators are high order functions
@about_bp.route("/")
def about_page():
    return render_template("about.html", users=users)


@about_bp.route("/<id>")
def about_page_details(id):
    filtered_user = next((user for user in users if user["id"] == id), None)
    if filtered_user:
        return render_template("filtered_about.html", user=filtered_user)
